INSERT INTO AVENGER (ID, LANG, NAME, ACTOR, DESCRIPTION, URLIMAGE, COLOR_1, COLOR_2, COLOR_3, COLOR_4, COLOR_5) VALUES (8, 'SP', 'Capitán América', 'Chris Evans', 'Capitán América (en inglés Captain America), cuyo nombre real es Steven Grant Rogers, mejor conocido como Steve Rogers, es un superhéroe y justiciero ficticio que aparece en cómics estadounidenses publicados por Marvel Comics. Creado por los historietistas Joe Simon y Jack Kirby, el personaje apareció por primera vez en Captain America Comics #1 (marzo de 1941) de Timely Comics, predecesor de Marvel Comics...', 'https://i.pinimg.com/564x/ed/3c/7b/ed3c7b715072235c671ea97277753905.jpg', 4279324633, 4282088868, 4294243571, 4294901760, 4287306274);
